import React, { useEffect } from 'react';
import { Navigate, useLocation } from 'react-router-dom';
import { useAuth } from '../../contexts/AuthContext';

const ProtectedRoute = ({ children, adminOnly = false }) => {
  const { isAuthenticated, user, loading, checkAuthStatus } = useAuth();
  const location = useLocation();

  useEffect(() => {
    // Only attempt to verify token if we're not already authenticated and not currently loading
    if (!loading && !isAuthenticated) {
      checkAuthStatus(true);
    }
  }, [isAuthenticated, loading, checkAuthStatus]);

  // Show loading state while auth is being checked
  if (loading) {
    return (
      <div data-testid="auth-loading" className="flex items-center justify-center h-screen">
        <div className="animate-spin rounded-full h-12 w-12 border-t-2 border-b-2 border-primary-500"></div>
      </div>
    );
  }

  if (!isAuthenticated) {
    // Redirect them to the /login page, but save the current location they were
    // trying to go to when they were redirected. This allows us to send them
    // along to that page after they login.
    return <Navigate to="/login" state={{ from: location }} replace data-testid="auth-redirect" />;
  }

  // Add admin check if required
  if (adminOnly && user?.role !== 'admin') {
     // Redirect non-admins away from admin routes
     return <Navigate to="/" replace data-testid="admin-redirect" />;
  }

  return <div data-testid="protected-content">{children}</div>;
};

export default ProtectedRoute;
