import React from 'react';

const DateRangeSelector = ({ startDate, endDate, onStartDateChange, onEndDateChange, onRangeChange }) => {
  const handlePredefinedRange = (range) => {
    const now = new Date();
    let start = new Date();

    switch(range) {
      case 'week':
        start.setDate(now.getDate() - 7);
        break;
      case 'month':
        start.setMonth(now.getMonth() - 1);
        break;
      case 'quarter':
        start.setMonth(now.getMonth() - 3);
        break;
      case 'year':
        start.setFullYear(now.getFullYear() - 1);
        break;
      default:
        // Last 30 days by default
        start.setDate(now.getDate() - 30);
    }

    onRangeChange(start.toISOString().split('T')[0], now.toISOString().split('T')[0]);
  };

  return (
    <div className="flex flex-col sm:flex-row gap-4 items-center mb-6">
      <div className="flex space-x-2">
        <button 
          onClick={() => handlePredefinedRange('week')} 
          className="px-3 py-1 text-xs font-medium rounded bg-gray-100 dark:bg-gray-700 hover:bg-gray-200 dark:hover:bg-gray-600"
        >
          Last Week
        </button>
        <button 
          onClick={() => handlePredefinedRange('month')} 
          className="px-3 py-1 text-xs font-medium rounded bg-gray-100 dark:bg-gray-700 hover:bg-gray-200 dark:hover:bg-gray-600"
        >
          Last Month
        </button>
        <button 
          onClick={() => handlePredefinedRange('quarter')} 
          className="px-3 py-1 text-xs font-medium rounded bg-gray-100 dark:bg-gray-700 hover:bg-gray-200 dark:hover:bg-gray-600"
        >
          Last Quarter
        </button>
        <button 
          onClick={() => handlePredefinedRange('year')} 
          className="px-3 py-1 text-xs font-medium rounded bg-gray-100 dark:bg-gray-700 hover:bg-gray-200 dark:hover:bg-gray-600"
        >
          Last Year
        </button>
      </div>

      <div className="flex items-center space-x-2">
        <div>
          <label className="block text-xs text-gray-500 dark:text-gray-400 mb-1">Start Date</label>
          <input
            type="date"
            value={startDate}
            onChange={(e) => onStartDateChange(e.target.value)}
            className="px-2 py-1 text-sm border border-gray-300 dark:border-gray-600 rounded dark:bg-gray-700 dark:text-white"
          />
        </div>
        <span className="text-gray-500 dark:text-gray-400">to</span>
        <div>
          <label className="block text-xs text-gray-500 dark:text-gray-400 mb-1">End Date</label>
          <input
            type="date"
            value={endDate}
            onChange={(e) => onEndDateChange(e.target.value)}
            className="px-2 py-1 text-sm border border-gray-300 dark:border-gray-600 rounded dark:bg-gray-700 dark:text-white"
          />
        </div>
      </div>
    </div>
  );
};

export default DateRangeSelector;
