import React, { createContext, useState, useContext, useEffect, useCallback } from 'react';
import apiRequest, { setAuthToken, getAuthToken } from '../utils/api';

const AuthContext = createContext(null);

export const AuthProvider = ({ children }) => {
  const [user, setUser] = useState(null);
  const [loading, setLoading] = useState(true);
  const [token, setTokenState] = useState(getAuthToken());
  const [isAuthenticated, setIsAuthenticated] = useState(!!getAuthToken());

  const handleSetToken = (newToken) => {
    setAuthToken(newToken);
    setTokenState(newToken);
    setIsAuthenticated(!!newToken);
  };

  // Function to fetch user profile based on token
  const fetchUserProfile = useCallback(async () => {
    if (!token) {
      setUser(null);
      setLoading(false);
      setIsAuthenticated(false);
      return;
    }

    setLoading(true);
    try {
      // Make sure this endpoint matches your backend URL structure
      const userData = await apiRequest('/accounts/profile/', 'GET');
      console.log('User profile fetched successfully:', userData);
      setUser(userData);
      setIsAuthenticated(true);
    } catch (error) {
      console.error("Failed to fetch user profile:", error);
      // If token is invalid, log out
      if (error.status === 401 || error.status === 403) {
        console.log('Authentication error - clearing token');
        handleSetToken(null);
        setUser(null);
        setIsAuthenticated(false);
      }
    } finally {
      setLoading(false);
    }
  }, [token]);

  useEffect(() => {
    fetchUserProfile();
  }, [fetchUserProfile]);

  const login = async (email, password) => {
    try {
      console.log('Attempting login with:', email);

      // Make sure this endpoint matches your backend login endpoint
      const data = await apiRequest('/accounts/login/', 'POST', { email, password }, false);
      console.log('Login response:', data);

      if (data && data.access) {
        handleSetToken(data.access);
        // Fetch profile after setting token
        await fetchUserProfile();
        return true;
      } else {
        console.error("Login response missing access token:", data);
        throw new Error("Login failed: No token received");
      }
    } catch (error) {
      console.error("Login failed:", error);
      handleSetToken(null);
      setUser(null);
      setIsAuthenticated(false);
      throw error;
    }
  };

  const logout = useCallback(async () => {
    try {
      // Optional: notify the backend about logout
      await apiRequest('/accounts/logout/', 'POST');
    } catch (error) {
      console.error("Logout API call failed:", error);
      // Continue with local logout even if API call fails
    } finally {
      handleSetToken(null);
      setUser(null);
      setIsAuthenticated(false);
    }
  }, []);

  const register = async (userData) => {
    try {
      // Make sure this endpoint matches your backend registration endpoint
      const response = await apiRequest('/accounts/register/', 'POST', userData, false);
      console.log('Registration successful:', response);
      return response;
    } catch (error) {
      console.error("Registration failed:", error);
      throw error;
    }
  };

  const value = {
    user,
    loading,
    isAuthenticated,
    login,
    logout,
    register,
    fetchUserProfile,
  };

  return <AuthContext.Provider value={value}>{children}</AuthContext.Provider>;
};

export const useAuth = () => {
  const context = useContext(AuthContext);
  if (!context) {
    throw new Error('useAuth must be used within an AuthProvider');
  }
  return context;
};
