import React, { useState, useEffect } from 'react';
import { Link } from 'react-router-dom';
import apiRequest from '../../utils/api';
import DashboardLayout from '../../components/layouts/DashboardLayout';
import Breadcrumb from '../../components/ui/Breadcrumb';

const BillingDashboard = () => {
  const [summaryData, setSummaryData] = useState({
    unbilledAmount: 0,
    outstandingAmount: 0,
    monthlyRevenue: 0
  });
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    const fetchSummaryData = async () => {
      try {
        // You can implement this API endpoint later
        // const response = await apiRequest('/api/billing/summary');
        // setSummaryData(response);

        // For now we'll use placeholder data
        setSummaryData({
          unbilledAmount: 1250.00,
          outstandingAmount: 3475.00,
          monthlyRevenue: 5200.00
        });
      } catch (error) {
        console.error('Error fetching billing summary:', error);
      } finally {
        setLoading(false);
      }
    };

    fetchSummaryData();
  }, []);

  const billingFeatures = [
    {
      title: 'Time Entries',
      description: 'Record and manage billable hours spent on cases',
      icon: 'clock',
      link: '/billing/time-entries',
    },
    {
      title: 'Invoices',
      description: 'Create and manage client invoices',
      icon: 'document',
      link: '/billing/invoices',
    },
    {
      title: 'Reports',
      description: 'View billing reports and analytics',
      icon: 'chart',
      link: '/billing/reports',
    },
  ];

  const renderIcon = (iconName) => {
    switch (iconName) {
      case 'clock':
        return (
          <svg xmlns="http://www.w3.org/2000/svg" className="h-6 w-6" fill="none" viewBox="0 0 24 24" stroke="currentColor">
            <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M12 8v4l3 3m6-3a9 9 0 11-18 0 9 9 0 0118 0z" />
          </svg>
        );
      case 'document':
        return (
          <svg xmlns="http://www.w3.org/2000/svg" className="h-6 w-6" fill="none" viewBox="0 0 24 24" stroke="currentColor">
            <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M9 12h6m-6 4h6m2 5H7a2 2 0 01-2-2V5a2 2 0 012-2h5.586a1 1 0 01.707.293l5.414 5.414a1 1 0 01.293.707V19a2 2 0 01-2 2z" />
          </svg>
        );
      case 'chart':
        return (
          <svg xmlns="http://www.w3.org/2000/svg" className="h-6 w-6" fill="none" viewBox="0 0 24 24" stroke="currentColor">
            <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M9 19v-6a2 2 0 00-2-2H5a2 2 0 00-2 2v6a2 2 0 002 2h2a2 2 0 002-2zm0 0V9a2 2 0 012-2h2a2 2 0 012 2v10m-6 0a2 2 0 002 2h2a2 2 0 002-2m0 0V5a2 2 0 012-2h2a2 2 0 012 2v14a2 2 0 01-2 2h-2a2 2 0 01-2-2z" />
          </svg>
        );
      default:
        return null;
    }
  };

  return (
    <DashboardLayout>
      <div className="px-4 py-4 sm:px-6 lg:px-8">
        <Breadcrumb
          items={[
            { name: 'Home', href: '/' },
            { name: 'Billing', href: '/billing' },
          ]}
        />

        <div className="mt-4">
          <h1 className="text-2xl font-semibold text-gray-900">Billing Dashboard</h1>
          <p className="mt-2 text-sm text-gray-700">
            Manage time entries, create invoices, and track payments
          </p>
        </div>

        <div className="mt-6 grid grid-cols-1 gap-5 sm:grid-cols-2 lg:grid-cols-3">
          {billingFeatures.map((feature) => (
            <Link
              key={feature.title}
              to={feature.link}
              className="bg-white overflow-hidden shadow rounded-lg hover:shadow-md transition-shadow duration-200"
            >
              <div className="p-5">
                <div className="flex items-center">
                  <div className="flex-shrink-0 bg-primary-100 rounded-md p-3 text-primary-600">
                    {renderIcon(feature.icon)}
                  </div>
                  <div className="ml-5">
                    <h3 className="text-lg font-medium text-gray-900">{feature.title}</h3>
                    <p className="mt-1 text-sm text-gray-500">{feature.description}</p>
                  </div>
                </div>
              </div>
            </Link>
          ))}
        </div>

        <div className="mt-8 bg-white shadow overflow-hidden rounded-lg">
          <div className="px-4 py-5 sm:p-6">
            <h3 className="text-lg leading-6 font-medium text-gray-900">Billing Summary</h3>
            <div className="mt-5 grid grid-cols-1 gap-5 sm:grid-cols-3">
              <div className="border rounded-lg p-4 bg-gray-50">
                <p className="text-sm font-medium text-gray-500">Unbilled Time</p>
                <p className="mt-1 text-2xl font-semibold text-gray-900">${loading ? '--' : summaryData.unbilledAmount.toFixed(2)}</p>
              </div>
              <div className="border rounded-lg p-4 bg-gray-50">
                <p className="text-sm font-medium text-gray-500">Outstanding Invoices</p>
                <p className="mt-1 text-2xl font-semibold text-gray-900">${loading ? '--' : summaryData.outstandingAmount.toFixed(2)}</p>
              </div>
              <div className="border rounded-lg p-4 bg-gray-50">
                <p className="text-sm font-medium text-gray-500">Revenue This Month</p>
                <p className="mt-1 text-2xl font-semibold text-gray-900">${loading ? '--' : summaryData.monthlyRevenue.toFixed(2)}</p>
              </div>
            </div>
          </div>
        </div>

        {/* Recent Activity */}
        <div className="mt-8 bg-white shadow overflow-hidden rounded-lg">
          <div className="px-4 py-5 sm:px-6">
            <h3 className="text-lg font-medium text-gray-900">Recent Activity</h3>
          </div>
          <div className="border-t border-gray-200">
            <div className="bg-gray-50 py-12 text-center">
              <p className="text-gray-500">No recent billing activity to display.</p>
            </div>
          </div>
        </div>
      </div>
    </DashboardLayout>
  );
};

export default BillingDashboard;
