import axios from 'axios';

// Base API URL - update to match your backend
const API_BASE_URL = 'http://localhost:8000/api'; // Adjust this to match your backend URL

// Token storage functions
export const setAuthToken = (token) => {
  if (token) {
    localStorage.setItem('auth_token', token);
  } else {
    localStorage.removeItem('auth_token');
  }
};

export const getAuthToken = () => {
  return localStorage.getItem('auth_token');
};

// Create axios instance with default config
const api = axios.create({
  baseURL: API_BASE_URL,
  headers: {
    'Content-Type': 'application/json',
  },
});

// Add request interceptor to attach token
api.interceptors.request.use(
  (config) => {
    const token = getAuthToken();
    if (token) {
      config.headers['Authorization'] = `Bearer ${token}`;
    }
    return config;
  },
  (error) => {
    return Promise.reject(error);
  }
);

// API request helper
const apiRequest = async (endpoint, method = 'GET', data = null, authRequired = true) => {
  try {
    console.log(`Making ${method} request to ${endpoint}`);

    // If auth is required but no token exists
    if (authRequired && !getAuthToken()) {
      console.error('Authentication required but no token found');
      throw { message: 'Authentication required', status: 401 };
    }

    const config = {
      url: endpoint,
      method: method,
      data: method !== 'GET' ? data : null,
      params: method === 'GET' && data ? data : null,
    };

    const response = await api(config);
    return response.data;
  } catch (error) {
    console.error('API request failed:', error);

    // Handle error responses
    if (error.response) {
      console.error('Error response:', error.response.data);
      throw {
        status: error.response.status,
        data: error.response.data,
        message: error.response.data.detail || 'Request failed'
      };
    }

    throw {
      status: 500,
      message: error.message || 'Network error',
      data: null
    };
  }
};

export default apiRequest;
